css:
   

        body {
            font-family: Arial, sans-serif;
            padding: 0px;
            font-size: 12px;
        }
        .print-format{
            padding:0;
        }
        .main-contentwrapper {
            border: 5px solid gray;
            /*width:100%;*/
            height:100vh;
            color: #303841c5;
            background: #e5f4ff;
            box-shadow:none;
        }

        .border-none {
            border: none !important;
        }

        .section-title {
            text-align: center;
            font-weight: bold;
            font-size: 16px;
            color: #303841c5;
            padding-bottom: 6px;
        }

        .border-red {
            border-bottom: 5px solid #c81f84;
        }

        .border-blue {
            border-bottom: 5px solid #0267B1;
        }

       .student-intro caption {
            margin-top: 12px;
            font-weight: bold;
            font-size: 14px;
            color: #303841c5;
            border-top: 2px solid gray;
            border-bottom: 2px solid gray;
            padding: 6px;
            text-align:center ;
        }

        input.tableinput,
        textarea.tableinput {
            width: 100%;
            border: none;
            background: transparent;
            font-weight: bold;
            font-size: 12px;
            color: #000;
            resize: none;
            overflow: hidden;
            text-align: center;
        }

        input.tableinput:focus,
        textarea.tableinput:focus {
            border: none;
            outline: none;
        }

        .student-intro input.tableinput {
            background-color: transparent;
            border-bottom: 1px solid gray;
        }

        .student-intro {
            margin-bottom: 6px;
        }

        table {
            width: 100%;
            border-collapse: collapse;
        }

        td,
        th {
            border: 1px solid gray;
            padding: 4px;
            text-align: center;
            vertical-align: middle;
        }
        .blnone{
            border-left: none !important;
        }
        .brnone{
            border-right: none;
        }

        textarea.tableinput {
            white-space: pre-wrap;
            overflow-wrap: break-word;
            min-height: 20px;
        }
        
        body:last-child .print-format td img{
            width:unset !important;
        }
        .tableinput {
          pointer-events: none; 
          user-select: none;    
        }
        @media print {

            textarea.tableinput,
            input.tableinput {
                overflow: visible !important;
            }
        td,
        th {
            border: 1px solid gray;
            padding: 4px;
            text-align: center;
            vertical-align: middle;
        }
        
            html, body {
        margin: 0 !important;
        padding: 0 !important;
        background: #fff !important;
        color-adjust: exact !important;
        -webkit-print-color-adjust: exact !important;
        print-color-adjust: exact !important;
        box-shadow: none !important;
    }

    * {
        box-shadow: none !important;
    }

    .main-contentwrapper {
        border: 5px solid gray !important;
        background: #e5f4ff !important;
        width: 100%;
        height: auto;
        box-shadow: none !important;
    }

    td, th {
        border: 1px solid gray !important;
        padding: 4px !important;
        text-align: center;
        vertical-align: middle;
    }

    .blnone {
        border-left: none !important;
    }

    .brnone {
        border-right: none !important;
    }

    @page {
        size: A4;
        margin: 0;
    }
    .tableinput {
  pointer-events: none; /* Disable clicks */
  user-select: none;    /* Prevent text selection */
  background: #f5f5f5;  /* Gray background to indicate non-editable */
}
