css:
  body {
      font-family: Arial, sans-serif;
      padding: 20px;
      background: #e0f2a9;
    }

    .certificate {
      width: 100%;
      max-width: 980px;
      margin: auto;
      background-color: rgba(0, 123, 255, 0.5);

      backdrop-filter: blur(10px);
  
      -webkit-backdrop-filter: blur(50px);

      border: 10px solid #2186c0e0
    }

    .certificatebody {
      padding: 0 18px 10px 18px;
      background-color: #e5f4ff;

    }

    h2,
    h3 {
      text-align: center;
      color: #007cc3;
      margin-bottom: 10px;
      font-size: 30px;
      font-weight: bold;
      margin-top: 0px;
    }

    p {
      color: white;
      text-align: center;
      margin: 0px;
      font-size: 14px;
    }

    table {
      border-collapse: collapse;
      margin-bottom: 10px;
      width: 100%;
    }

    td {
      padding: 6px;
      white-space: nowrap;
      /* font-weight: 600; */
      /* text-align: end; */
      margin-top: 10px;
      font-size: 16px;
      color: #727272;
    }

    input {
      border: none;
      border-bottom: 1px solid #000;
      background: transparent;
      font-weight: bold;
      width: 100%;
      color: #757474;

    }

    input:focus {
      border-bottom: 1px solid black;
      /* Blue border */
      outline: none;
      /* Optional: removes default blue glow */
    }
    .print-format {
    background-color: white;
    border-radius: 8px;
    max-width: 8.3in;
    min-height: 11.69in;
    padding: 0;
}

    @media print {
      input {
        border-bottom: 1px solid black;
      }

      body {
        background: none;
        padding: 0;

      }

      .certificate {
        border: none;
        box-shadow: none;
        padding: 20px;
      }
    }

    .w-50 {
      width: calc(100% - 209px);
    }

    /* .lastsection{
      width: 100%;
    } */

    .lastsection th p {
      text-align: center;
      font-weight: bold;
      margin-bottom: 20px;
      font-size: 18px;
      color: rgb(0, 126, 126);
    }

    .lastsection td input {
      width: unset;
      margin-bottom: 10px;
    }

    .sign {
      text-align: center;
    }
