css:
  body {
      font-family: Arial, sans-serif;
      padding: 0px;
  
    }

    .certificate {
      width: 100%;
      max-width: 980px;
      margin: auto;
      backdrop-filter: blur(10px);
      -webkit-backdrop-filter: blur(50px);
      border: 10px solid #2186c0;
      border-bottom:none;

    }
     .certificatefooter{
      background: #2186c0;
      padding: 10px;
      font-size: 16px;
      color: white;
      text-align: center;
    } 
    .certificate .intro{
          background-color: rgba(0, 123, 255, 0.5);
    }
    .certificate .intro table{
       margin:0;
    }

    .certificatebody {
      padding: 0 8px 10px 8px;
      background-color: #e5f4ff;

    }

   .print-format h2,
    .print-format h3 {
      text-align: center;
      color: #007cc3;
      margin-bottom: 10px;
      font-size: 24px;
      font-weight: bold;
      margin-top: 0px;
    }

    p {
      color: white;
      text-align: center;
      margin: 0px;
      font-size: 14px;
    }

    table {
      border-collapse: collapse;
      margin-bottom: 10px;
      width: 100%;
    }

    td {
      padding: 6px;
      white-space: nowrap;
       font-weight: 600; 
      /* text-align: end; */
      margin-top: 10px;
      font-size: 14px;
      color: #0064b4;
      color: #4483b6;
    }

   .certificate p.pinput {
      border: none;
      border-bottom: 1px solid #000;
      background: transparent;
      font-weight: 600;
      width: 100%;
      color: #0064b4;
      display:inline-block;
    }

    input:focus {
      border-bottom: 1px solid black;
      /* Blue border */
      outline: none;
      /* Optional: removes default blue glow */
    }
    .print-format {
    background-color: white;
    border-radius: 8px;
    max-width: 8.3in;
    min-height: 11.69in;
    padding: 0;
}
.print-format td, .print-format th{
    padding:6px !important;
}
 .certificate p.pinput.w-50 {
      width: calc(100% - 167px);
    }

    /* .lastsection{
      width: 100%;
    } */

    .lastsection th p {
      text-align: center;
      font-weight: bold;
      margin-bottom: 20px;
      font-size: 18px;
      color:#005b99;
    }

    .lastsection td input {
      width: unset;
      margin-bottom: 10px;
    }

    .sign {
      text-align: center;
          width: 50%
    }
    .cstmwid{
        width:1px;
    }

@media print {
  body {
    margin: 0;
    padding: 0;
    background: none !important;
  }

  .containr, .certificate {
    page-break-inside: avoid;
    box-shadow: none;
    margin: 0;
    padding: 0;
  }
  input {
    border: none;
    border-bottom: 1px solid black;
    color: black;
  }

  .print-format {
    width: 100%;
    max-width: 100%;
    min-height: auto;
  }

    .lastsection th p {
        text-align: center;
        font-weight: bold;
        margin-bottom: 20px;
        font-size: 18px;
        color: #005b99 !important;
    }

  @page {
    size: A4;
    margin: 0;
  }
}
