css:
       .print-format{
        padding:0;   
       }
       table.noborder tr td{
           border:none;
       }
       table.noborder tr td p{
           border-bottom:1px solid black;
           width:100%;
       }
       /*table.noborder tr td:nth-child(odd) {*/
       /*    background: #f0f0f0;*/
       /*    width:20%;*/
       /*}*/
       .print-preview{
           background:transparent;
       }
        .all-content {
            background-color: #E6F2FF;
            padding: 24px;
            font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
            /*height: 299mm;   */
            height: 100vh;   
         
        }

        .all-content table {
            width: 100%;
            border-collapse: collapse;
            /* margin-bottom: 0px; */
        }
        .all-content h2{
            font-weight:bold;
        }
        .print-format td{
             padding: 6px !important;
        }
        .all-content td
            {
                color: #003366;
                border: 1px solid #000000;
                padding: 6px;
                vertical-align: middle;
                font-size: 14px;
                height: 50px;
            }
            .print-format .all-content td {
                 vertical-align: middle !important;
            }

        tr {
            text-align: center;
        }


        .section-title {
            background-color: #6699CC;
            color: #FFFFFF;
            font-weight: bold;
            text-align: center;
            padding: 6px;
            border-left: 1px solid black;
            border-right: 1px solid black;
        }
        .no-border {
            border: none !important;
            vertical-align: middle;

        }

        input.tableinput {
            /*height: 30px;*/
            border: none;
            background: transparent;
            font-weight: bold;
            width: 100%;
            color: #000000;
            /*color: #004080;*/
            box-sizing: border-box;
            font-size:12px;
            text-align: center;
        }
        textarea.tableinput {
          border: none;
          background: transparent;
          font-weight: bold;
          width: 100%;
          color: #000000;
          box-sizing: border-box;
          font-size: 12px;
          text-align: center;
          resize: none;
          overflow: hidden; /* hide scrollbar */
          white-space: pre-wrap;
          overflow-wrap: break-word;
          min-height: 30px;
          padding: 4px;
        }

          textarea.tableinput:focus {
            border: none;
            outline: none;
        }


        input.bottominput {
            width: 27%;
            margin-inline-end: 12px;
            border-bottom: 2px solid #011b35;
            font-weight: bold;

        }

        input:focus {
            border: none;
            outline: none;
        }

        input.bottominput:focus {
            width: 27%;
            margin-inline-end: 12px;
            border-bottom: 2px solid black;
            font-weight: bold;
        }
        .contact{
            text-align: center;
            font-size: 14px;
            margin-top:24px;
            font-weight:bold;
        }
        .contact a{
            color:blue;
            text-decoration:none;
        }
        .contact a:hover{
            color:blue;
            text-decoration:none;
        }
        @media print {
            .all-content {
                width: 210mm;       /* A4 width */
                height: 297mm;      /* A4 height */
                padding: 10mm;      /* safe margin */
                box-sizing: border-box;
                page-break-after: avoid;
              }
            .section-title {
                /*color: #000000;*/
                  background-color: #6699CC;
            font-weight: bold;
            text-align: center;
            padding: 6px;
            }
            .all-content table tr td strong
            {
                color: #003366;
            }

            .all-content table {
                width: 100%;
                border-collapse: collapse;
                border: 1px solid black;
                /* margin-bottom: 0px; */
            }

            input.bottominput {
                width: 26%;
                margin-inline-end: 12px;
                border-bottom: 1px solid #0e141a;
                font-weight: bold;

            }
              body {
                margin: 0;
                padding: 0;
                background: none !important;
              }

              .print-format {
                width: 100%;
                max-width: 100%;
                min-height: auto;
              }
              @page {
                size: A4;
                margin: 0;
              }
         
        }
