css:
@import url('https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css'); /* Matched with your CSS import preference */

@page {
  size: A4;
  margin: 0; /* Removes default printer margins */
}

html, body {
  width: 100%;
  height: 100%; /* Crucial for body and print-format to take full page height */
  margin: 0;
  padding: 0;
  -webkit-print-color-adjust: exact !important; /* Ensures background graphics are printed in WebKit browsers */
  print-color-adjust: exact !important; /* Standard property for printing background graphics */
}

body {
  /*background: radial-gradient(circle, #0b6c3b 0%, #1e4a30 100%);*/
  font-family: Arial, sans-serif;
  background-repeat:no-repeat
}
.w-50{
    width:50%;
}
.w-100{
    width:100%;
}
.print-format {
    font-size: 13px;
    background:transparent !important;
    padding:0px !important;
}
.print-fomat {
  background: radial-gradient(circle, #0b6c3b 0%, #1e4a30 100%) !important;
  padding:26px !important;
  width: 100%;
  height: 100%; /* Takes full height of the A4 page */
  box-sizing: border-box; /* Padding and border are included in the element's total width and height */
  color: white;
  overflow: hidden; /* This will clip content if it's too large for one page.
                       It's better to adjust content (image sizes, font sizes, spacing)
                       to naturally fit on one A4 page. Remove if clipping is not desired. */
  -webkit-print-color-adjust: exact !important; /* Ensure background for this specific element prints */
  print-color-adjust: exact !important;

}

.print-fomat .top-logo {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 15px; /* Adjusted slightly, can be tuned further */
}

.print-fomat .top-logo img {
  max-width: 150px; /* Adjust as needed to fit and look good */
  height: auto;

}

.print-fomat .text-center img { /* Style for the health card image */
  display: block; /* For centering with margin auto if needed */
  margin-left: auto;
  margin-right: auto;
    width:88%;
}

/*FORM SECTION INPUT FIELD*/

.print-fomat .bg-color{
    background-color:white;
    border-radius:10px;
    padding:16px;
    width:80%;
    margin:auto;
    margin-bottom:16px;
}
.print-fomat .d-flex{
 display:flex;
 
}
.print-fomat .field-row {
  display: flex;
  align-items: center;
  margin-bottom: 10px; 
  font-family: Arial, sans-serif;
}

.print-fomat .field-label {
  font-weight: 600;
  font-size: 15px; 
  margin-inline: 16px;
  margin-bottom:-20px;
  flex-shrink: 0;

}

.print-fomat .field-input {
  flex: 1;
  border: none;
  border-bottom: 1px solid black;
  outline: none;
  font-size: 15px; 
  padding: 4px; 
  background: transparent;
  color: black;
  width:80%;
}

.print-fomat .bottom-section{
    background:yellow;
    color:black;
    padding:12px;
    border-radius:10px;
    text-align:center;
}

@media print {
  body {
    font-size: 10pt;
  }

  .field-label, .field-input {
    font-size: 10pt; /* Adjust form field fonts for print if needed */
  }

  .top-logo {
    margin-bottom: 10px;
  }
  .text-center {
      margin-bottom: 15px !important; /* Bootstrap's mb-4 might be too large */
  }

.print-fomat {
    width: 100%;
    height: 100%;
    box-sizing: border-box;
    padding: 36px;
    color: white;
    overflow: hidden;
    -webkit-print-color-adjust: exact !important;
    print-color-adjust: exact !important;
    padding-bottom: 0;
    width: 794px;
height: 1123px;
}
}

/*===================================================================================================================================*/
/*hygenic custom sectiom*/
.hygiene-field{
   display: flex;
    flex-direction:column;
}
.hygiene-item-row{
  display: flex;
  justify-content: space-between;
}
.hygiene-description{
    margin-right:12px;
}
.fw-bold{
    font-weight:bold !important;
}
.d-flex{
    display:flex;
}
.page-container {
    width: 210mm; /* A4 width */
    min-height: 290mm; /* A4 height, adjusted slightly for content fit, can be 297mm */
    margin: 0 auto; /* Center page on screen */
    padding: 12mm 10mm; /* Padding inside the page (top/bottom, left/right) */
    background-color: white;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1); /* Shadow for screen view */
    box-sizing: border-box;
}

.form-header {
    text-align: center;
    margin-bottom: 10px; /* Reduced margin */
}

.form-header h1 {
    font-size: 13pt; /* Slightly smaller */
    font-weight: bold;
    margin: 0 0 5px 0; /* Reduced margin */
    padding-bottom: 0;
    border-bottom: 1.5px solid #555;
    display:inline;
}

.sub-header {
    text-align: center;
    font-size: 11pt;
    font-weight: bold;
    margin-top: 8px; /* Reduced margin */
    margin-bottom: 8px; /* Reduced margin */
}

.form-section {
    margin-bottom: 8px; /* Reduced margin */
}

.field-row {
    display: flex;
    flex-wrap: wrap; /* Allow items to wrap if needed */
    justify-content: space-between;
    margin-bottom: 6px; /* Reduced margin */
    align-items: baseline; /* Align items based on their text baseline */
}

.field-item {
    display: flex;
    align-items: baseline; /* Align label and input line */
    margin-right: 5px; /* Space between items in a row */
    margin-bottom: 4px; /* Space if items wrap */
    flex-grow: 1; /* Allow items to grow */
    flex-basis: auto; /* Default basis */
}
.field-item:last-child {
    margin-right: 0;
}

/* Specific widths for multi-item rows to better match the image */
.personal-info .name, .personal-info .mother-name { flex-basis: 30%; }
.personal-info .child-id { flex-basis: 20%; }
.personal-info .father-name { flex-basis: 30%; }
.personal-info .class, .personal-info .gender, .personal-info .weight-now { flex-basis: 18%; }
.personal-info .height, .personal-info .age, .personal-info .aghosh { flex-basis: 25%; }


.field-item label {
    margin-right: 4px;
    white-space: nowrap; /* Prevent label text from wrapping */
    font-weight: normal; /* Labels are not bold in the image */
}


.print-form .field-input {
  flex: 1;
  border: none;
  border-bottom: 1px solid black;
  outline: none;
  font-size: 15px; /* Slightly reduced, adjust as needed */
  padding: 0; /* Slightly reduced, adjust as needed */
  background: transparent;
  color: black;
  width:100%;

.input-line.short { min-width: 40px; flex-grow: 0.5; }
.input-line.medium { min-width: 60px; flex-grow: 1; }
.input-line.wide { min-width: 80px; flex-grow: 2; }
.input-line.extra-short { min-width: 20px; flex-grow: 0.2; margin: 0 3px;}
.input-line.long-hygiene { min-width: 50px; flex-grow: 0.5;}


.field-item.full-width {
    flex-basis: 30% !important; /* For items like Clubbing, Cyanosis, etc. to fit 3 in a row */
}
.field-item.full-width .input-line {
    min-width: 80px; /* Ensure they have enough line space */
}


.helper-text {
    font-size: 8.5pt;
    color: #444;
    margin-left: 3px;
    white-space: nowrap;
}
.helper-text-inline {
    font-size: 8.5pt;
    color: #444;
    margin-left: 3px;
    white-space: nowrap;
    line-height: 1.6; /* Align with input line */
}

.helper-text-block {
    font-size: 8pt;
    color: #555;
    display: block; /* Puts helper text on new line if needed, or style as inline-block */
    clear: both; /* If floats were used, not necessary with flex */
    margin-left: 5px;
}


.complex-nose, .complex-jaundice {
    display: flex;
    align-items: baseline;
}
.complex-nose label, .complex-jaundice label { margin-right: 4px;}
.complex-nose .input-line.medium, .complex-jaundice .input-line.medium {flex-grow: 0.5;} /* Adjust for primary line */


/* Print Specific Styles */
@media print {
    body {
        background-color: white;
        padding: 0;
        margin: 0;
        font-size: 9.5pt; /* Base font size for print */
         -webkit-print-color-adjust: exact !important; /* Ensures backgrounds and colors print in Chrome/Safari */
        print-color-adjust: exact !important; /* Standard for printing backgrounds and colors */
    }

    @page {
        size: A4;
        margin: 0; /* Remove browser's default print margins */
    }

    .page-container {
        width: 210mm; /* A4 width */
        height: 297mm; /* A4 height */
        margin: 0;
        padding: 10mm; /* Margins inside the page for content */
        box-shadow: none; /* No shadow for print */
        overflow: hidden; /* Clip content that overflows the single page */
    }

    .form-header h1 {
        font-size: 12.5pt;
        /* border-bottom: 1px solid black; */
    }
    .sub-header {
        font-size: 10.5pt;
    }

    .helper-text, .helper-text-inline {
        font-size: 8pt;
        color: black;
    }
    .helper-text-block {
        font-size: 7.5pt;
        color: black;
    }
    .hygiene-field .helper-text-block.hygiene-description {
        font-size: 7pt;
    }

    .input-line {
        border-bottom-color: black;
    }
}





















.page-break {
  page-break-before: always;
  break-before: page;
}
