css:
        .print-format{
            padding:0;
        }
        .print-format td, .print-format th {
            padding: 6px !important;
            font-size: 14px;
      
            vertical-align: middle !important;
        }
       .print-format table tr td:not(:has(p)),.print-format table tr td span{
                 font-weight:600;
       }
        .main-wrapper {
            margin: auto;
            background-color: rgb(200 200 200 / 17%);
            width: 210mm;
            height: 297mm;
            padding-top: 0;
            color: rgb(87 87 87);

        }

        .section,.section1 {
            padding: 29px !important;
        }

        table {
            width: 100%;
        }

        .main-wrapper .header,.main-wrapper .mainwrapper-foter {
            background-color: #0060ae;
        }
         .main-wrapper .header h2{
             color:white;
            text-align: center;
         }
        .print-format .no-border p {
            margin: 12px 0px 3px;
        }

        .no-border {
            border: none !important;
        }

        .section.input-section {
            background-color: #E6F2FF;
        }

        .section table p {
            border: none;
            border-radius: 4px;
            box-sizing: border-box;
            width: 100%;
            padding: 8px;
            margin-bottom: 8px;
            color: #004080;
            text-align: center;
            background:white;
        }

        .section tr td .main-section-form, .last-section tr td .main-section-form {
            background: transparent;
            border:none;
            border-bottom: 1px solid black;
            border-radius: 0%;
            box-sizing: unset;
            padding: 0px;
            width:55%;
            display:inline-block;
        }
         .section tr td .main-section-form.large-width, .last-section tr td .main-section-form.large-width{
                width:70%
         }
        .last-section tr td{
            text-align: center;
        }
        /* for the second sextion inpur field*/
        .print-format .input-section p {
            margin: 3px 80px 0 0px;
        }

    
        .main-wrapper .mainwrapper-foter p{
                color: white;
                text-align: center; 
                font-size: 12px;
        }
        .bottom {
            width: 100%;
            position: absolute;
            bottom: 0;
            left: 0;
        }
                @media print {
            
              body {
                margin: 0;
                padding: 0;
                background: none !important;
              }

              .print-format {
                width: 100%;
                max-width: 100%;
                min-height: auto;
              }
              @page {
                size: A4;
                margin: 0;
              }
              .section,.section1{
                  padding:20px;
              }
         
        }
