.modal{
    height:auto!important;
}
.navbar.navbar-light.navbar-expand-lg,.top_header{
    display:none;
}
                                        /*all page content */

[data-doctype="Web Form"] .page-content-wrapper .container.my-4 .page_content {
    max-width:950px;
}                                   

                                        /*WEB FORM HEADER */

[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form-header
 {
    border: 0;
    border-bottom: none;
    /*background-color:transparent;*/
    padding: 24px;
    padding-bottom:0px;
}
[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form-header .web-form-head{
    border-bottom:none;
}

                                    /*WEB FORM FULL */

[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form {
    /*background-color: transparent;*/
    border:none ;
    border-radius:8px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    padding:24px;
    /*for footer progress bar*/
    position:relative;
    padding-top:40px;
    height:69vh;
    overflow: hidden auto;
}

                                    /*FORM INNER H1 TAG*/

[data-doctype="Web Form"] .page-content-wrapper .container .page_content h1 {
    font-size: 24px;
    font-weight:700;
    font-style:italic;
    margin-top: 0;
    margin-bottom: 0;
    padding-bottom: 2px;
}

                                    /*FORM HEARDER H1 SIDE INDICATOR*/
.indicator-pill.orange{
    background:#FEEBE3;
    border:1px solid #C00202;
    color:#C00202;
    font-weight:500;
    font-size:12px;
}

                                    /*WEB-FORM LABELS*/
	#page-student-applicant label {
		font-weight: 500;
		font-size: 16px;
		line-height: 100%;
		letter-spacing: 0;
		font-style: italic;
	}

	                                /*WEB-FORM INNER INPUT TAGS*/
 [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-wrapper .form-control{
		/*color: #B2B2B2;*/
		font-size: 16px;
		font-weight: 600;
		font-style:italic;
		line-height: 100%;
		letter-spacing: 0;
		padding: 16px;
		padding-top:0px;
		padding-bottom: 0;
		border:1px solid #D9D9D9 !important;
		background-color:#F8F8F8;
		/**/
        color: black !important;           /* White text */
       appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
	}
	[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-wrapper .form-control input:-webkit-autofill{
  -webkit-box-shadow: 0 0 0 1000px #000 inset !important;
  -webkit-text-fill-color: #fff !important;
}



	                                       /*bottom buttons*/

     [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .btn-default{
         background-color:#C00202;
         color:#FFEDED;
         font-weight:700;
         padding-inline: 26px;
     }

     [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .btn-next{
         background-color:#224976;
         color:#FFFFFF;
         font-weight:700;
     }


	                                    /*last section progress bar*/

	[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress{
	  width: 100%;
    justify-content: center;
	}
	[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area{
	position: absolute;
    top: 12px;
    width: 100%;
    }                               

                                         /*SAME PROPERTY FOR SINGLE LINE ACTIVE AND NON ACTIVE */
     [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step{
         width:10%;
         height:8px;
     }
    /*defauld circle border converted in line of active*/
     [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step.active{
         border:none;
         background:#224976;
         margin:0;
     }

    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step[data-step-id="4"] {
        background-color:transparent;
        width: 30px;
        border-color: transparent;
    }                                                                 
                                        /*DEFAULT CIRLCLE CONVERTED IN LINE OF ALL OTHER  */
     [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step{
         width:10%;
         height:8px;
         background-color:#D0DEEB;
         margin:0;
     }
                                        /*active line after moving to next step*/
[data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step.step-success:not(.active) {
    background-color: #224976;
    border:none;
}


                                         /*PROGRESS BAR MIDDILE CIRLCE FOR ALL OTHER*/
     [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step .slide-step-indicator{
         width:30px;
         height:30px;
         background-color:#D0DEEB;
         position: relative;
         right:45%;

     }

                                    /*PROGRESS BAR MIDDILE CIRLCE FOR ACTIVE */
     [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step.active .slide-step-indicator{
         width:30px;
         height:30px;
         background-color:#224976;
         position: relative;
         right: 45%;

    } 

                                                 /*CIRLCLE INNER COUNTING 1-5*/
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step:nth-child(1) .slide-step-indicator::after{
           content: "1";
            color: #012257;
            text-align: center;
            position: absolute;
            left: 33%;
            top: 12%;
            font-weight:600;
            font-style:italic;
    }
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step:nth-child(2) .slide-step-indicator::after{
           content: "2";
            color: #012257;
            text-align: center;
            position: absolute;
            left: 33%;
            top: 12%;
            font-weight:600;
            font-style:italic;
    }
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step:nth-child(3) .slide-step-indicator::after{
           content: "3";
            color: #012257;
            text-align: center;
            position: absolute;
            left: 33%;
            top: 12%;
            font-weight:600;
            font-style:italic;
    }
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step:nth-child(4) .slide-step-indicator::after{
           content: "4";
            color: #012257;
            text-align: center;
            position: absolute;
            left: 33%;
            top: 12%;
            font-weight:600;
            font-style:italic;
    }
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step:nth-child(5) .slide-step-indicator::after{
           content: "5";
            color: #012257;
            text-align: center;
            position: absolute;
            left: 33%;
            top: 12%;
            font-weight:600;
            font-style:italic;
    }

                                         /* ACTIVE COUNTING NUMBER COLOR*/
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step.active .slide-step-indicator::after{
            color: white;
    }








    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step#0 .slide-step-complete{
      content:"1";  
      color:white;
    }

    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step.step-success:not(.active) .slide-step-indicator {
      display: block; background: #224976;
    }

    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step.step-success:not(.active) .slide-step-indicator::after {
     color: white;
    }
                                                            /*for the form top and bottom div height 3rem*/
    div.page_content .web-form-container > :last-child,div.page_content .web-form-container > :first-child {
     display:none;
    }
                                                /*for the progressbar tick icon*/
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step .slide-step-complete .icon-xs {
    margin-left:-14px;
}



                                            /*media query for lines on mobile screen taking full width*/
@media (max-width: 767.98px) {
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step {
        width: 19%;
        height: 8px;
        background-color: #D0DEEB;
        margin: 0;
    }
                                            /*for whole line to start */
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress {
    width: 100%;
    justify-content: flex-start;
    }
    /*on staging slide indicator hiding*/
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step.step-success:not(.active) .slide-step-indicator {
  display: block;
  background-color: #224976;
    }

                                            /*for circle width and height*/
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step .slide-step-indicator {
    width: 25px;
    height: 25px;
    }
    [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .center-area .slides-progress .slide-step.active .slide-step-indicator {
    width: 25px;
    height: 25px;
    }
}
@media (max-width: 436px) {
  [data-doctype="Web Form"] .page-content-wrapper .container .page_content .web-form .web-form-footer .web-form-actions .btn-default{
          padding-inline: 9px;
  }
